  abstract = {Radio communication remains the primary battery consuming activity in wireless systems. Advances in MAC protocols have enabled signiﬁcant lifetime improvements, but in systems with low data rate, idle listening, and other communication artifacts can begin to dominate costs. One proposal to combat this is the addition of a second, extremely low power radio component that is always-on. As a consequence of the extremely low power, such radios are incapable of decoding general data, and thus are often delegated the task of listening for a trigger, leading to the terminology wake-up radio, as this extremely low power radio is used to wake up a higher power radio, which is then used for data communication. While wake-up technology has been steadily evolving over the last decade in the hardware arena, few protocols have been developed to exploit it. In this work, we present WaCo, our wake-up radio COOJA extension that allows exploration of the capabilities of the wake-up radio from the desktop environment. We also use our extended simulator to concretely show the potential beneﬁts of the wake-up radio hardware with two, standard data collection protocols. Our results simultaneously conﬁrm that wake-up technology has tremendous potential and that our simulator extension provides an effective mechanism for such exploration.},
  pages = {6},
  author = {Piyare, Timofei Istomin Rajeev},
  file = {Piyare - WaCo A Wake-Up Radio COOJA Extension for Simulati.pdf:/home/aghiles/Aghiles/Redaction/lib/iot/storage/6DFT7MCS/Piyare - WaCo A Wake-Up Radio COOJA Extension for Simulati.pdf:application/pdf},
  groups = {Wakeup}
}

@article{buettner_xmac_,
  timestamp = {2019-06-28T16:47:15Z},
  langid = {english},
  title = {X-{{MAC}}: {{A Short PreambSleenMsAoCr NPertowtoocrkosl}} ∗for {{Duty}}-{{Cycled Wireless}}},
  abstract = {In this paper we present X-MAC, a low power MAC protocol for wireless sensor networks (WSNs). Standard MAC protocols developed for duty-cycled WSNs such as BMAC, which is the default MAC protocol for TinyOS, employ an extended preamble and preamble sampling. While this “low power listening” approach is simple, asynchronous, and energy-efﬁcient, the long preamble introduces excess latency at each hop, is suboptimal in terms of energy consumption, and suffers from excess energy consumption at nontarget receivers. X-MAC proposes solutions to each of these problems by employing a shortened preamble approach that retains the advantages of low power listening, namely low power communication, simplicity and a decoupling of transmitter and receiver sleep schedules. We demonstrate through implementation and evaluation in a wireless sensor testbed that X-MAC’s shortened preamble approach signiﬁcantly reduces energy usage at both the transmitter and receiver, reduces per-hop latency, and offers additional advantages such as ﬂexible adaptation to both bursty and periodic sensor data sources.},
  pages = {14},
  author = {Buettner, Michael and Yee, Gary V and Anderson, Eric and Han, Richard},
  file = {Buettner et al. - X-MAC A Short PreambSleenMsAoCr NPertowtoocrkosl .pdf:/home/aghiles/Aghiles/Redaction/lib/iot/storage/8AZQMW8U/Buettner et al. - X-MAC A Short PreambSleenMsAoCr NPertowtoocrkosl .pdf:application/pdf},
  groups = {houda}
}

