@article{kim_ssp_,
  timestamp = {2019-07-23T09:22:55Z},
  langid = {english},
  title = {{{SSP}}: {{Self}}-{{Sovereign Privacy}} for {{Internet}} of {{Things Using Blockchain}} and {{MPC}}},
  abstract = {With the proliferation of the Internet of Things (IoTs), their devices have been collecting our personal data to enhance our life styles and their purposes. Care must be taken when handling such sensitive data to protect the data owners’ privacy while guaranteeing the integrity when the data is accessed by a third party. Furthermore, data should be available without much delay to support medical emergencies. Unfortunately, IoT devices may be limited with storage and computational resources to operate highly secure operations and respond to data requests. In this paper, we propose SSP, a novel mechanism that protects the privacy and integrity of the data collected by IoT devices without having a single point of failure, while minimizing the cryptographic operations that must be performed on IoT devices. SSP utilizes the blockchain data structure for availability and integrity, and Multi-PartyComputations (MPC) for privacy protection. We implemented SSP in C/C++ and according to our evaluation results, SSP is efﬁcient in computation, communication, and storage operations on IoT devices.},
  pages = {8},
  author = {Kim, Tiffany Hyun-Jin and Laboratories, HRL},
  file = {Kim and Laboratories - SSP Self-Sovereign Privacy for Internet of Things.pdf:/home/aghiles/Aghiles/Redaction/lib/iot/storage/KMYWWAMK/Kim and Laboratories - SSP Self-Sovereign Privacy for Internet of Things.pdf:application/pdf},
  groups = {privacy}
}

@article{li_contractbased_,
  timestamp = {2019-07-23T09:23:00Z},
  langid = {english},
  title = {Contract-{{Based Approach}} for {{Security Deposit}} in {{Blockchain Networks}} with {{Shards}}},
  abstract = {As a decentralized ledger technology, blockchain is considered to be a potential solution for applications with highly concentrated management mechanism. However, most of the existing blockchain networks are employed with the hashpuzzle-solving consensus protocol, known as proof-of-work. The competition of solving the puzzle introduces high latency, which directly leads to a long transaction-processing time. One solution of this dilemma is to establish a blockchain network with shards. In this paper, we focus on the blockchain network with shards and adopt the security-deposit based consensus protocol, studying the problem of how to balance the security incentive and the economic incentive. Also, the inherent features of the blockchain, i.e., anonymity and decentralization, introduce the information asymmetric issue between the beacon chain and the participants. The contract theory is utilized to formulate the problem between them. As such, the optimal rewards related to the different types of validators can be obtained, as well as the reasonable deposits accordingly. Compared with the ﬁxed deposits, the ﬂexible deposits can provide enough economic incentive for the participants without losing the security incentives. Besides, the simulation results demonstrate that the contract theory approach is capable of maximizing the beacon chain’s utility and satisfying the incentive compatibility and individual rationality of the participants.},
  pages = {8},
  author = {Li, Jing},
  file = {Li - Contract-Based Approach for Security Deposit in Bl.pdf:/home/aghiles/Aghiles/Redaction/lib/iot/storage/G4ECPMZA/Li - Contract-Based Approach for Security Deposit in Bl.pdf:application/pdf},
  groups = {Blockchain}
}

@article{miehle_decentralized_,
  timestamp = {2019-07-23T09:23:16Z},
  langid = {english},
  title = {Toward a {{Decentralized Marketplace}} for {{Self}}-{{Maintaining Machines}}},
  abstract = {Industrial procurement processes are inefﬁcient, expensive, and error-prone as they require the agreement of numerous participants, audit-proof documentation, and are characterized by a high number of manual tasks. Blockchain technology has the potential to counter these challenges by combining peer-to-peer networks, cryptography, and consensus algorithms. A blockchain is a decentralized database that stores transactions and data transparently, chronologically, and tamperproof in a distributed network. In this paper, we present a blockchain-based industrial marketplace, where machines have an identity and account for selecting, ordering, and paying materials in an automated manner using smart contracts. This allows manufacturers with a low real-net-output-ratio, such as car manufacturers that depend on numerous suppliers, to optimize their purchasing processes. Using our case study, we demonstrate prototypical implementation of the decentralized marketplace and its applications in the area of industrial maintenance. Furthermore, we conducted a semi-structured interview with procurement and information technology experts from the automotive industry. The results indicate that our prototype offers a promising approach streamlining procurement processes within the next two to ﬁve years.},
  pages = {8},
  author = {Miehle, Daniel},
  file = {Miehle - Toward a Decentralized Marketplace for Self-Mainta.pdf:/home/aghiles/Aghiles/Redaction/lib/iot/storage/GBLU3QVQ/Miehle - Toward a Decentralized Marketplace for Self-Mainta.pdf:application/pdf},
  groups = {Blockchain}
}

@article{jalalzai_proteus_,
  timestamp = {2019-07-23T09:24:41Z},
  langid = {english},
  title = {Proteus: {{A Scalable BFT Consensus Protocol}} for {{Blockchains}}},
  abstract = {Byzantine Fault Tolerant (BFT) consensus exhibits higher throughput in comparison to Proof of Work (PoW) in blockchains. However, BFT-based protocols suffer from scalability problems with respect to the number of replicas in the network due to their inherent quadratic message complexity. Previously, proposed solutions improve BFT performance for normal operation, but will fall back to quadratic message complexity once the protocol observes a certain number of failures. To address this issue we propose Proteus, a new BFT-based consensus protocol which elects a subset of nodes c as a root committee. Proteus guarantees stable performance, regardless of the number of failures in the network and it improves on the quadratic message complexity of typical BFT-based protocols to O(cn) messages, where c $<$$<$ n, for large number of nodes n. We tested our protocol on 200 Amazon EC2 instances, with two different baseline BFT protocols (PBFT and Bchain) for comparison. In these tests, our protocol outperformed the baselines by more than 2× in terms of throughput as well as latency.},
  pages = {6},
  author = {Jalalzai, Mohammad M and University, Louisiana State and Busch, Costas and University, Louisiana State and Iii, Golden G Richard and University, Louisiana State},
  file = {Jalalzai et al. - Proteus A Scalable BFT Consensus Protocol for Blo.pdf:/home/aghiles/Aghiles/Redaction/lib/iot/storage/XNYL32W9/Jalalzai et al. - Proteus A Scalable BFT Consensus Protocol for Blo.pdf:application/pdf},
  groups = {framework}
}

@article{harris_decentralized_,
  timestamp = {2019-07-23T09:25:10Z},
  langid = {english},
  title = {Decentralized \& {{Collaborative AI}} on {{Blockchain}}},
  abstract = {Machine learning has recently enabled large advances in artiﬁcial intelligence, but these tend to be highly centralized. The large datasets required are generally proprietary; predictions are often sold on a per-query basis; and published models can quickly become out of date without effort to acquire more data and re-train them. We propose a framework for participants to collaboratively build a dataset and use smart contracts to host a continuously updated model. This model will be shared publicly on a blockchain where it can be free to use for inference. Ideal learning problems include scenarios where a model is used many times for similar input such as personal assistants, playing games, recommender systems, etc. In order to maintain the model’s accuracy with respect to some test set we propose both ﬁnancial and non-ﬁnancial (gamiﬁed) incentive structures for providing good data. A free and open source implementation for the Ethereum blockchain is provided at https://github.com/microsoft/0xDeCA10B.},
  pages = {8},
  author = {Harris, Justin D and Waggoner, Bo},
  file = {Harris and Waggoner - Decentralized & Collaborative AI on Blockchain.pdf:/home/aghiles/Aghiles/Redaction/lib/iot/storage/SZHZWZE7/Harris and Waggoner - Decentralized & Collaborative AI on Blockchain.pdf:application/pdf},
  groups = {Blockchain}
}

@article{teerapittayanon_daimon_,
  timestamp = {2019-07-23T09:25:35Z},
  langid = {english},
  title = {{{DaiMoN}}: {{A Decentralized Artificial Intelligence Model Network}}},
  abstract = {We introduce DaiMoN, a decentralized artiﬁcial intelligence model network, which incentivizes peer collaboration in improving the accuracy of machine learning models for a given classiﬁcation problem. It is an autonomous network where peers may submit models with improved accuracy and other peers may verify the accuracy improvement. The system maintains an append-only decentralized ledger to keep the log of critical information, including who has trained the model and improved its accuracy, when it has been improved, by how much it has improved, and where to ﬁnd the newly updated model. DaiMoN rewards these contributing peers with cryptographic tokens. A main feature of DaiMoN is that it allows peers to verify the accuracy improvement of submitted models without knowing the test labels. This is an essential component in order to mitigate intentional model overﬁtting by model-improving peers. To enable this model accuracy evaluation with hidden test labels, DaiMoN uses a novel learnable Distance Embedding for Labels (DEL) function proposed in this paper. Speciﬁc to each test dataset, DEL scrambles the test label vector by embedding it in a low-dimension space while approximately preserving the distance between the dataset’s test label vector and a label vector inferred by the classiﬁer. It therefore allows proof-of-improvement (PoI) by peers without providing them access to true test labels. We provide analysis and empirical evidence that under DEL, peers can accurately assess model accuracy. We also argue that it is hard to invert the embedding function and thus, DEL is resilient against attacks aiming to recover test labels in order to cheat. Our prototype implementation of DaiMoN is available at https://github.com/steerapi/daimon.},
  pages = {8},
  author = {Teerapittayanon, Surat and University, Harvard and Kung, H T and University, Harvard},
  file = {Teerapittayanon et al. - DaiMoN A Decentralized Artificial Intelligence Mo.pdf:/home/aghiles/Aghiles/Redaction/lib/iot/storage/JQX2ZGE9/Teerapittayanon et al. - DaiMoN A Decentralized Artificial Intelligence Mo.pdf:application/pdf},
  groups = {framework}
}

@article{islam_remote_,
  timestamp = {2019-07-23T09:25:47Z},
  langid = {english},
  title = {Remote {{Configuration}} of {{Integrated Circuit Features}} and {{Firmware Management}} via {{Smart Contract}}},
  abstract = {The cost to develop a new integrated circuit (IC), its fabrication, debug and volume production has been escalating with scaling of transistor feature size. According to an IBS report, the cost of developing a System on Chip (SoC) at 14nm may be as high as \$300 million [1]. The economics of semiconductor IC development favors high volume production, while high volume cannot be attained without developing an IC that serves a large number of applications. Some of these applications are in lowmargin Internet of Things (IoT) devices, where an SoC cannot command a high price. Consequently, without the ability to customize IC features after production, the price of an IC will be determined by its lowest priced application. This motivates the manufacturers to develop capabilities for post-production IC customization. The commodity microprocessor business offers an example of post-production customization, where a manufacturer can tailor cache size, number of cores and frequency of operation for a target market segment after a chip has been manufactured. Today, such customization is limited to one-time programming (OTP) for predetermined IC bins. In this paper, we explore how an IC can be programmed repeatedly and securely using a blockchain-based smart contract. This will enable users to upgrade IC features, or rent upgraded IC features for a ﬁxed period after it has been purchased. The availability of such a system could, for example, allow a buyer to upgrade her processor from i3 to i5 after it has been purchased to scale to her computing needs in exchange of a payment made to the manufacturer. IC feature conﬁguration is implemented by ﬁrmware updates from the manufacturer. The smart contract takes the feature conﬁguration request from the IC as input and outputs the source of corresponding ﬁrmware. To support remote and authorized update by manufacturer, we propose an on-die hardware module that communicates with the smart contract and enforces its functionalities. Availability of this module also facilitates secure ﬁrmware update. The blockchain makes the update protocol secure and prevents users from obtaining unauthorized update. Index Terms—Blockchain, Smart Contract, Firmware Update, Chip Features, Internet of Things.},
  pages = {7},
  author = {Islam, Nazmul},
  file = {Islam - Remote Configuration of Integrated Circuit Feature.pdf:/home/aghiles/Aghiles/Redaction/lib/iot/storage/KS7NWJUK/Islam - Remote Configuration of Integrated Circuit Feature.pdf:application/pdf},
  groups = {Blockchain}
}

@article{truong_secure_,
  timestamp = {2019-07-23T09:25:59Z},
  langid = {english},
  title = {Towards {{Secure}} and {{Decentralized Sharing}} of {{IoT Data}}},
  abstract = {The Internet of Things (IoT) bears unprecedented security and scalability challenges due to the magnitude of data produced and exchanged by IoT devices and platforms. Some of those challenges are currently being addressed by coupling IoT applications with blockchains. However, current blockchainbacked IoT systems simply use the blockchain to store access control policies, thereby underutilizing the power of blockchain technology. In this paper, we propose a new framework named Sash that couples IoT platforms with blockchain that provides a number of advantages compared to state of the art. In Sash, the blockchain is used to store access control policies and take access control decisions. Therefore, both changes to policies and access requests are correctly enforced and publicly auditable. Further, we devise a “data marketplace” by leveraging the ability of blockchains to handle ﬁnancial transaction and providing “by design” remuneration to data producers. Finally, we exploit a special ﬂavor of identity-based encryption to cater for cryptography-enforced access control while minimizing the overhead to distribute decryption keys. We prototype Sash by using the FIWARE open source IoT platform and the Hyperledger Fabric framework as the blockchain back-end. We also evaluate the performance of our prototype and show that it incurs tolerable overhead in realistic deployment settings.},
  pages = {8},
  author = {Truong, Hien Thi Thu and Europe, NEC Laboratories and Almeida, Miguel and Europe, NEC Laboratories and Karame, Ghassan and Europe, NEC Laboratories and Soriente, Claudio and Europe, NEC Laboratories},
  file = {Truong et al. - Towards Secure and Decentralized Sharing of IoT Da.pdf:/home/aghiles/Aghiles/Redaction/lib/iot/storage/B4L6M6I2/Truong et al. - Towards Secure and Decentralized Sharing of IoT Da.pdf:application/pdf},
  groups = {privacy}
}

@article{linoy_scalable_,
  timestamp = {2019-07-23T09:26:14Z},
  langid = {english},
  title = {Scalable {{Privacy}}-{{Preserving Query Processing}} over {{Ethereum Blockchain}}},
  abstract = {Blockchain technologies have recently received considerable attention, partly due to the success of cryptocurrency applications such as Bitcoin and Ethereum. As the adoption of blockchain technologies by various sectors increases, there is a demand for tools that enable regulatory enforcement, which include monitoring, examining and ensuring compliance of the data stored by the blockchain systems, all in a privacy preserving way. Current blockchain solutions store transactions in append-only and immutable fashion without any indexing, which contributes to limited and inefﬁcient queries. Additionally, there is no support for privacypreserving query processing. To address these issues, in this paper, we propose a system that can provide auditors, enforcing regulatory compliance, with efﬁcient, scalable and richer blockchain query processing over Hadoop and synchronized Ethereum clients. The system additionally ensures auditors’ privacy by utilizing cryptography techniques over semi-trusted servers to protect the auditors’ identities, queries and their results.},
  pages = {7},
  author = {Linoy, Shlomi},
  file = {Linoy - Scalable Privacy-Preserving Query Processing over .pdf:/home/aghiles/Aghiles/Redaction/lib/iot/storage/TSNDBMST/Linoy - Scalable Privacy-Preserving Query Processing over .pdf:application/pdf},
  groups = {privacy}
}

@article{feng_joint_,
  timestamp = {2019-07-23T09:30:00Z},
  langid = {english},
  title = {Joint {{Service Pricing}} and {{Cooperative Relay Communication}} for {{Federated Learning}}},
  abstract = {For the sake of protecting data privacy and due to the rapid development of mobile devices, e.g., powerful central processing unit (CPU) and nascent neural processing unit (NPU), collaborative machine learning on mobile devices, e.g., federated learning, has been envisioned as a new AI approach with broad application prospects. However, the learning process of the existing federated learning platforms rely on the direct communication between the model owner, e.g., central cloud or edge server, and the mobile devices for transferring the model update. Such a direct communication may be energy inefﬁcient or even unavailable in mobile environments. In this paper, we consider adopting the relay network to construct a cooperative communication platform for supporting model update transfer and trading. In the system, the mobile devices generate model updates based on their training data. The model updates are then forwarded to the model owner through the cooperative relay network. The model owner enjoys the learning service provided by the mobile devices. In return, the mobile devices charge the model owner certain prices. Due to the coupled interference of wireless transmission among the mobile devices that use the same relay node, the rational mobile devices have to choose their relay nodes as well as deciding on their transmission powers. Thus, we formulate a Stackelberg game model to investigate the interaction among the mobile devices and that between the mobile devices and the model owner. The Stackelberg equilibrium is investigated by capitalizing on the exterior point method. Moreover, we provide a series of insightful analytical and numerical results on the equilibrium of the Stackelberg game.},
  pages = {6},
  author = {Feng, Shaohan and University, Nanyang Technological and Niyato, Dusit and University, Nanyang Technological and Wang, Ping and University, York and Kim, Dong In and University, Sungkyunkwan and Liang, Ying-Chang},
  file = {Feng et al. - Joint Service Pricing and Cooperative Relay Commun.pdf:/home/aghiles/Aghiles/Redaction/lib/iot/storage/9KYSTAAZ/Feng et al. - Joint Service Pricing and Cooperative Relay Commun.pdf:application/pdf},
  groups = {Mesh/WSN}
}

@article{zitouni_iotbased_,
  timestamp = {2019-07-23T09:30:48Z},
  langid = {english},
  title = {{{IoT}}-{{Based Urban Traffic}}-{{Light Control}}: {{Modelling}}, {{Prototyping}} and {{Evaluation}} of {{MQTT Protocol}}},
  abstract = {Most trafﬁc light’s control systems in smart cities are wired and have a semi-static behavior. They are time-based, with pre-conﬁgured pattern and expensive cameras. Although trafﬁc lights can communicate wirelessly with incoming vehicles, they are less adapted to an urban environment. If we consider light signs as an Internet of Things (IoT) network, one issue is to model thoroughly the change of signs’ states and the Quality of Service (QoS) of this network. In this paper, we propose a new architecture of Urban Trafﬁc Light Control based on an IoT network (IoT-UTLC). The objective is to interconnect both roads’ infrastructures and trafﬁc lights through an IoT platform. We designed our IoT-UTLC by selecting motes and protocols of wireless sensor network (WSN). Message Queuing Telemetry Transport (MQTT) protocol has been integrated to manage QoS. It enables lights to adapt remotely to any situation and smoothly interrupt trafﬁc light’s classic cycles. Our experimental results show that the MQTT protocol is efﬁcient when the packets rate exceeds 35\% of trafﬁc ﬂow, it reduces trafﬁc delay up to 0.05s at 90\% of congestion. After veriﬁcation and validation of our solution using a UPPAAL model checker, our system has been prototyped. Motes’ functions have been implemented on Contiki OS and connected via a 6LoWPAN/IEEE 802.15.4 network. Timestamping messages have been performed throughout the system to evaluate the MQTT protocol with different QoS levels. In our experiments, we measured the Round-trip delay time (RTT) of messages exchanged between the WSN and IoT Cloud. The results show that MQTT decreases the RTT when the Cumulative Distributed Function (CDF) of generated messages exceeds 35\%.},
  pages = {8},
  author = {Zitouni, Rafik and Paris, ECE and Petit, Jérémy and Paris, ECE and Djoudi, Aghiles and Paris, ECE},
  file = {Zitouni et al. - IoT-Based Urban Traffic-Light Control Modelling, .pdf:/home/aghiles/Aghiles/Redaction/lib/iot/storage/6GGKF6P5/Zitouni et al. - IoT-Based Urban Traffic-Light Control Modelling, .pdf:application/pdf},
  groups = {Rafik}
}

@article{zhang_qlearning_,
  timestamp = {2019-07-23T09:31:41Z},
  langid = {english},
  title = {Q-{{Learning Based Energy Harvesting}} for {{Heterogeneous Statistical QoS Provisioning}} over {{Multihop Big}}-{{Data Relay Networks}}},
  abstract = {With the increasing demand for the data-intensive wireless multimedia services over the time-varying wireless channels, the big-data based wireless networks demand the 5G candidate framework to process such massive amount of multimedia data without causing extra burden to the backhaul links in supporting the heterogeneous statistical delay-bounded quality-of-service (QoS) provisionings. Due to the beneﬁts of energy harvesting (EH) technologies, wireless devices are able to support the dataintensive wireless multimedia services by harvesting energy from the environment. Energy harvesting has emerged as the promising technology to solve the energy supply problem while bringing new challenges due to the stochastic nature of the harvested energy in supporting the heterogeneous statistical quality-of-service (QoS) provisionings. However, due to the unknown dynamics of the harvested energy as well as the channel state information (CSI), it is challenging to design the efﬁcient routing protocol for selecting the optimal routing and power allocation policies under the statistical delay-bounded QoS constraints. To overcome the aforementioned problems, in this paper we propose the Q-learning based optimal routing and power allocation policies through learning from the history of the energy harvesting process while satisfying the heterogeneous statistical delay-bounded QoS constraints over multihop big-data relay networks. In particular, under the heterogeneous statistical delay-bounded QoS requirements, we formulate the endto-end effective-capacity optimization problem for the batteryfree energy harvesting based big-data multihop relay networks. Then, we apply the Markov decision process and Q-learning methods for deriving the optimal multihop routing algorithms over big-data multihop relay networks. Also conducted is a set of simulations which evaluate the system performances and show that our proposed Q-learning based multihop routing scheme outperforms the other existing schemes under the heterogeneous statistical delay-bounded QoS constraints over multihop big-data relay networks.},
  pages = {8},
  author = {Zhang, Xi and Wang, Jingqing and Zhu, Qixuan},
  file = {Zhang et al. - Q-Learning Based Energy Harvesting for Heterogeneo.pdf:/home/aghiles/Aghiles/Redaction/lib/iot/storage/W74AYANG/Zhang et al. - Q-Learning Based Energy Harvesting for Heterogeneo.pdf:application/pdf},
  groups = {Mesh/WSN}
}

@article{malky_evaluation_,
  timestamp = {2019-07-23T09:31:46Z},
  langid = {english},
  title = {Evaluation of {{Precalibrated Electrochemical Gas Sensors}} for {{Air Quality Monitoring Systems}}},
  abstract = {Harmful atmospheric pollutants have been attributed to causing adverse weather conditions and health problems, which are particularly prevalent in more populated global regions such as in megacities. Nongovernmental and international agencies likewise throughout the world have called for increased awareness and action in effectively countering the increased rates of air pollution of which the consequences seem ominous. Meanwhile, in several major urban landscapes such as Saudi Arabia, no modern apparatus or efficient systematic mechanisms for qualitative detection of dangerous pollutants and gases exist. The current counterparts for hazardous gas measurements are expensive, bulky and cumbersome to set up. Thus, sensor devices that are portable and prompter in ambient air measurement are desired for filling the current gap in effective air pollution monitoring. Our objective in this research was to better understand and explore relatively cost-effective, flexible and dynamic sensors in real time monitoring that can efficiently function in modern day environments and regions. The suitability of these new-age sensors were analyzed for gases such as nitrogen dioxide (NO2), sulfur dioxide (SO2), carbon monoxide (CO), particle pollution (PM2.5, PM10), and ozone (O3). The study area was urban cities.},
  pages = {7},
  author = {Malky, Saeed},
  file = {Malky - Evaluation of Precalibrated Electrochemical Gas Se.pdf:/home/aghiles/Aghiles/Redaction/lib/iot/storage/Y9VB35JA/Malky - Evaluation of Precalibrated Electrochemical Gas Se.pdf:application/pdf},
  groups = {Weather}
}

@article{novo_enabling_,
  timestamp = {2019-07-23T09:32:35Z},
  langid = {english},
  title = {Enabling {{CoAP}}-{{Based Communication}} across {{Network Boundaries}}: {{Challenges}} and {{Solutions}}},
  abstract = {The depletion of the pool of unallocated IPv4 addresses has introduced new challenges in the deployment of the Internet of Things across IPv6 and IPv4 networks. In previous work we developed a CoAP-based transition architecture speciﬁcally designed to overcome some of these challenges. In this respect, this article extends that work identifying the most common IoT scenarios and their requirements to successfully achieve seamless cross-domain communication in IoT. Furthermore, an analysis of our architecture and the state-of-the-art network transition technologies is conducted on the basis of those requirements. Among other aspects, the analysis provides new insights of the shortcomings of our architecture. Based on these shortcomings, the article proposes new extensions of the architecture in order to achieve seamless communication across the most common IoT scenarios.},
  pages = {9},
  author = {Novo, Oscar and University, Aalto and Research, Ericsson},
  file = {Novo et al. - Enabling CoAP-Based Communication across Network B.pdf:/home/aghiles/Aghiles/Redaction/lib/iot/storage/I9MDYI3V/Novo et al. - Enabling CoAP-Based Communication across Network B.pdf:application/pdf},
  groups = {Weather}
}

@article{gu_general_,
  timestamp = {2019-07-23T09:35:51Z},
  langid = {english},
  title = {A {{General Framework}} for {{Adjustable Neighbor Discovery}} in {{Wireless Sensor Networks}}},
  abstract = {Wireless sensor networks have been widely adopted in real-life applications. As one of the fundamental processes in constructing the network, neighbor discovery is to ﬁnd out the existence of the nearby nodes. In order to enlarge the lifetime of each node, the nodes switch the radio OFF for most of the time, and only turn the radio ON for necessary communications. The fraction of time that the radio in ON is called duty cycle and low duty cycle schedule could help save energy. Most works focus on designing efﬁcient discovery schedule under a pre-deﬁned low duty cycle such that the neighboring nodes can discover each other in a short time. In this paper, we study a more practical problem where each node could adjust the duty cycle dynamically by the remaining energy or the subsequent tasks, which is referred to as adjustable neighbor discovery. We ﬁrst propose a general framework to handle the problem, then present two distributed algorithms that can ensure the discovery between the neighboring nodes, no matter when they start and what duty cycle they choose. We also conduct simulations to evaluate the algorithms and the results corroborate our analyses.},
  pages = {6},
  author = {Gu, Zhaoquan and University, Guangzhou and Wang, Yuexuan and University, Zhejiang and Tang, Keke and University, Guangzhou and Li, Chao and University, Guangzhou and Li, Mohan and University, Guangzhou and Yin, Lihua and University, Guangzhou},
  file = {Gu et al. - A General Framework for Adjustable Neighbor Discov.pdf:/home/aghiles/Aghiles/Redaction/lib/iot/storage/26NQTF2F/Gu et al. - A General Framework for Adjustable Neighbor Discov.pdf:application/pdf},
  groups = {Mesh/WSN}
}

@article{wang_lra3c_,
  timestamp = {2019-07-23T09:36:01Z},
  langid = {english},
  title = {{{LRA}}-{{3C}}: {{Learning Based Resource Allocation}} for {{Communication}}-{{Computing}}-{{Caching Systems}}},
  abstract = {The diversiﬁed usage scenarios of 5G networks require to allocate communication-computing-caching (3C) multidimensional resources efﬁciently according to different service requirement in terms of latency, bandwidth, and connectivity. In this paper, a network slicing (NS) architecture based mobile edge computing (MEC) and software-deﬁned network (SDN) technologies are proposed to support ﬂexible 3C resource allocation for improving the service of three 5G typical usage scenarios, namely enhanced mobile broadband (eMBB), ultra-reliable and low latency communications (URLLC) and massive machine-type communications (mMTC). Furthermore, a neural network (NN) based 3C resource allocation algorithm is designed to provide resource allocation decision for the NS architecture. With the aid of data pre-processing techniques, fast resource allocation decisions can be made by the NN aided NS architecture. Meanwhile, the performance of the proposed NS architecture is investigated in a testbed environment. Experimental results obtained from the testbed demonstrate that the system performance of the NN aided NS architecture can be improved by function ﬁtting based pre-processing approaches. Moreover, an accurate classiﬁcation performance can be obtained by the proposed architecture for facilitating 3C resource allocations.},
  pages = {6},
  author = {Wang, Ge},
  file = {Wang - LRA-3C Learning Based Resource Allocation for Com.pdf:/home/aghiles/Aghiles/Redaction/lib/iot/storage/874HCRFM/Wang - LRA-3C Learning Based Resource Allocation for Com.pdf:application/pdf},
  groups = {Untitled}
}

@article{behera_energyefficient_,
  timestamp = {2019-07-23T09:37:10Z},
  langid = {english},
  title = {Energy-{{Efficient Routing}} for {{Greenhouse Monitoring Using Heterogeneous Sensor Networks}}},
  abstract = {A suitable environment for the growth of plants is the Greenhouse, that needs to be monitored by a continuous collection of data related to temperature, carbon dioxide concentration, humidity, illumination intensity using sensors, preferably in a wireless sensor network (WSN). Demand initiates various challenges for diversified applications of WSN in the field of IoT (Internet of Things). Network design in IoT based WSN faces challenges like limited energy capacity, hardware resources, and unreliable environment. Issues like cost and complexity can be limited by using sensors that are heterogeneous in nature. Since replacing or recharging of nodes in action is not possible, heterogeneity in terms of energy can overcome crucial issues like energy and lifetime. In this paper, an energy efficient routing process is discussed that considers three different sensor node categories namely normal, intermediate and advanced nodes. Also, the basic cluster head (CH) selection threshold value is modified considering important parameters like initial and residual energy with an optimum number of CHs in the network. When compared with routing algorithms like LEACH (Low Energy Adaptive Clustering Hierarchy) and SEP (Stable Election Protocol), the proposed model performs better for metrics like throughput, network stability and network lifetime for various scenarios.},
  pages = {6},
  author = {Behera, Trupti Mayee},
  file = {Behera - Energy-Efficient Routing for Greenhouse Monitoring.pdf:/home/aghiles/Aghiles/Redaction/lib/iot/storage/QZMKD2ZX/Behera - Energy-Efficient Routing for Greenhouse Monitoring.pdf:application/pdf},
  groups = {Mesh/WSN}
}

